import React from 'react';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import {
  HomeIcon,
  DocumentDuplicateIcon,
  PlusCircleIcon,
  UserCircleIcon,
  Cog6ToothIcon
} from '@heroicons/react/24/outline';

const TabBar = () => {
  const { user } = useAuth();
  const isAdmin = user?.role === 'admin';

  // Tabs for non-admin users
  const tabs = [
    { name: 'Ana Sayfa', to: '/', icon: HomeIcon },
    { name: 'Destek Kayıtları', to: '/tickets', icon: DocumentDuplicateIcon },
    { name: 'Yeni Kayıt', to: '/tickets/new', exact: true, icon: PlusCircleIcon },
    { name: 'Profil', to: '/profile', icon: UserCircleIcon },
  ];

  // Additional tab for admin users
  const adminTab = { name: 'Yönetim', to: '/admin', icon: Cog6ToothIcon };

  // If admin, insert the admin tab before the profile tab
  const finalTabs = isAdmin 
    ? [...tabs.slice(0, 3), adminTab, tabs[3]] 
    : tabs;

  return (
    <div className="md:hidden fixed bottom-0 left-0 right-0 z-50 bg-white border-t border-gray-200 py-3" style={{ position: 'fixed', bottom: 0, width: '100%', borderTopWidth: '1px', borderTopColor: '#E5E7EB', boxShadow: '0 -2px 10px rgba(0,0,0,0.05)' }}>
      <div className="flex justify-evenly items-center max-w-md mx-auto px-2">
        {finalTabs.map((tab) => (
          <NavLink
            key={tab.name}
            to={tab.to}
            className={({ isActive }) => {
              // For routes with 'exact: true', only match exact path
              const isActiveLink = tab.exact 
                ? window.location.pathname === tab.to
                : isActive;

              return `flex flex-col items-center justify-center py-2 px-1 rounded-md w-full ${isActiveLink ? 'text-primary' : 'text-gray-600'}`;
            }}
          >
            {({ isActive }) => {
              // For routes with 'exact: true', only match exact path
              const isActiveLink = tab.exact 
                ? window.location.pathname === tab.to
                : isActive;

              return (
                <div className={`flex flex-col items-center transition duration-200 ${isActiveLink ? 'scale-110' : ''}`}>
                  {tab.name === 'Yeni Kayıt' ? (
                    <div className="bg-primary rounded-full p-2 -mt-3 shadow-md">
                      <tab.icon 
                        className="h-7 w-7 text-white" 
                        aria-hidden="true" 
                      />
                    </div>
                  ) : (
                    <tab.icon 
                      className={`h-7 w-7 transition-colors duration-200 ${isActiveLink ? 'text-primary' : 'text-gray-500'}`} 
                      aria-hidden="true" 
                    />
                  )}
                  {isActiveLink && tab.name !== 'Yeni Kayıt' && <div className="h-1 w-5 bg-primary rounded-t-full mt-1"></div>}
                </div>
              );
            }}
          </NavLink>
        ))}
      </div>
    </div>
  );
};

export default TabBar;
