import React from 'react';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import {
  HomeIcon,
  DocumentDuplicateIcon,
  PlusCircleIcon,
  UserCircleIcon,
  Cog6ToothIcon
} from '@heroicons/react/24/outline';

const TabBar = () => {
  const { user } = useAuth();
  const isAdmin = user?.role === 'admin';

  // Tabs for non-admin users
  const tabs = [
    { name: 'Ana Sayfa', to: '/', icon: HomeIcon },
    { name: 'Destek Kayıtları', to: '/tickets', icon: DocumentDuplicateIcon },
    { name: 'Yeni Kayıt', to: '/tickets/new', exact: true, icon: PlusCircleIcon },
    { name: 'Profil', to: '/profile', icon: UserCircleIcon },
  ];

  // Additional tab for admin users
  const adminTab = { name: 'Yönetim', to: '/admin', icon: Cog6ToothIcon };

  // If admin, insert the admin tab before the profile tab
  const finalTabs = isAdmin 
    ? [...tabs.slice(0, 3), adminTab, tabs[3]] 
    : tabs;

  return (
    <div className="md:hidden fixed bottom-0 left-0 right-0 z-50 bg-white border-t border-gray-200 py-2 shadow-lg" style={{ position: 'fixed', bottom: 0, width: '100%', borderTopWidth: '1px', borderTopColor: '#E5E7EB' }}>
      <div className="flex justify-evenly items-center max-w-md mx-auto px-2">
        {finalTabs.map((tab) => (
          <NavLink
            key={tab.name}
            to={tab.to}
            className={({ isActive }) => {
              // For routes with 'exact: true', only match exact path
              const isActiveLink = tab.exact 
                ? window.location.pathname === tab.to
                : isActive;

              return `flex flex-col items-center justify-center py-2 px-1 rounded-md w-full ${isActiveLink ? 'text-primary' : 'text-gray-600'}`;
            }}
          >
            {({ isActive }) => (
              <div className={`flex flex-col items-center transition duration-200 ${isActive ? 'scale-110' : ''}`}>
                <tab.icon 
                  className={`h-6 w-6 transition-colors duration-200 ${isActive ? 'text-primary' : 'text-gray-500'}`} 
                  aria-hidden="true" 
                />
                <span 
                  className={`mt-1 text-xs font-medium transition-colors duration-200 ${isActive ? 'text-primary' : 'text-gray-500'}`}
                  style={{ fontSize: '0.65rem' }}
                >
                  {tab.name}
                </span>
                {isActive && <div className="h-1 w-4 bg-primary rounded-t-full mt-1"></div>}
              </div>
            )}
          </NavLink>
        ))}
      </div>
    </div>
  );
};

export default TabBar;
