import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Spinner from '../../components/ui/Spinner';
import api from '../../services/api';

/**
 * PDF Yönlendirme Bileşeni
 * 
 * E-postadaki PDF linki ile servis kaydı detayındaki PDF indirme butonunun
 * aynı görevi görmesini sağlamak için yönlendirme yapar.
 * 
 * URL parametresindeki token bilgisini kullanarak API üzerinden PDF'e erişir.
 */
const PDFRedirect = () => {
  const { id, token } = useParams();

  useEffect(() => {
    const downloadPDF = async () => {
      try {
        // API URL'i oluştur
        const apiBaseUrl = process.env.REACT_APP_API_URL || '/api';
        const pdfUrl = `${apiBaseUrl}/tickets/${id}/pdf/${token}`;

        // PDF indirme işlemini başlat
        window.location.href = pdfUrl;
      } catch (error) {
        console.error('PDF yönlendirme hatası:', error);
        alert('PDF indirilirken bir hata oluştu.');
      }
    };

    downloadPDF();
  }, [id, token]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="text-center p-8 bg-white rounded-lg shadow-md">
        <Spinner size="lg" className="mb-4" />
        <h2 className="text-xl font-semibold text-gray-800 mb-2">PDF İndiriliyor</h2>
        <p className="text-gray-600">Lütfen bekleyin, PDF dosyanız hazırlanıyor...</p>
      </div>
    </div>
  );
};

// Burada default export yenileniyor
export default PDFRedirect;
